mapper.select:
select

from use_time
where userId = #{userId}
AND pointType = #{pointType}
AND TO_DAYS(useTime) = TO_DAYS(#{useTime})

SELECT
DATE_FORMAT(createTime,'%Y-%m-%d') AS time,  COUNT(*) AS count
FROM use_time
WHERE userId = #{userId}
AND pointType = #{pointType}
AND createTime BETWEEN #{startTime}  AND   #{endTime}
GROUP BY(DATE_FORMAT(createTime,'%Y-%m-%d'))
